            time: 0,
            speed: null,
            speedHealth: 100,
            speedLigh: true,
            showSpeed: false,
            showTips: true,
            speedText: 300,
            speedType: 'км/ч',
            speedLocked: false,
            voice: true,
            tansactionCash: 0,
            tansactionBank: 0,
            keyTipShow: false,
            players: 1,
            keyName: 'e',
            fuel: 0,
            cartridges: null,
            vehicleTips: false,
            data: '',
            education: false,
            educationTitle: '',
            educationTasks: []
        };
    },
    methods: {

    },
    watch: {
    },
    computed: {
        textBank() {
            if(!this.bank) return `0`;
            let n = this.bank + "";
            n = n.replace(/(\d)(?=(\d\d\d)+([^\d]|$))/g, '$1 ');;
            return `${n}`
        },
        textCash() {
            if(!this.money) return `0`;
            let n = this.money + "";
            n = n.replace(/(\d)(?=(\d\d\d)+([^\d]|$))/g, '$1 ');;
            return `${n}`
        },
        speedPercent(){
            return (100/400*parseInt(this.speedText))+"%"
        }
    },
    created(){
        window.hud = this;
        mp.on("HUD::CHANGE_MONEY",(money)=>{
            hud.money = money;
        })
        window.keyTip = (is,keyName = 'e')=>{
            this.keyTipShow = is;
            this.keyName = keyName;
        }
        let timer = setInterval(updateTime, 1000); 
        updateTime(); 

        function updateTime() { 
            if(!hud.show)return
            let cd = new Date(); 
            cd.setHours(cd.getHours() + 3, cd.getMinutes() + cd.getTimezoneOffset())
            let hours = cd.getHours();
            if(hours == 24)hours = 0;
            hud.time = zeroPadding(hours, 2) + ':' + zeroPadding(cd.getUTCMinutes(), 2);
            hud.date = zeroPadding(cd.getDate(), 2)+ '.' + zeroPadding(cd.getUTCMonth() + 1, 2) + '.' +   zeroPadding(cd.getFullYear(), 4) ; 
        }; 

        function zeroPadding(num, digit) { 
            let zero = Array(digit).fill('0').join('');
            return (zero + num).slice(-digit); 
        } 
    },
};
</script>